/**
 * Extracts the activity ID from a zone ID
 * e.g., "act43side_zone1" -> "act43side"
 */
export function getActivityIdFromZoneId(zoneId: string): string | null {
    const match = zoneId.match(/^([a-z0-9]+)_zone\d+$/i);
    return match?.[1] ?? null;
}

/**
 * Gets the human-readable event name for a zone ID
 * Falls back to the zone ID if no mapping exists
 */
export function getEventNameFromZoneId(zoneId: string): string {
    const activityId = getActivityIdFromZoneId(zoneId);
    if (activityId && ACTIVITY_NAMES[activityId]) {
        return ACTIVITY_NAMES[activityId];
    }
    return zoneId;
}

/**
 * Checks if an activity is a rerun
 */
export function isRerunActivity(zoneId: string): boolean {
    const activityId = getActivityIdFromZoneId(zoneId);
    return activityId ? RERUN_ACTIVITIES.has(activityId) : false;
}

