/**
 * Checks if an activity is currently open based on timestamps
 */
export function isActivityCurrentlyOpen(activityId: string, currentTime?: number): boolean {
    const times = ACTIVITY_EVENT_TIMES[activityId];
    if (!times) return false;
    const now = currentTime ?? Math.floor(Date.now() / 1000);
    return times.start <= now && now <= times.end;
}

/**
 * Gets the event times for an activity
 */
export function getActivityEventTimes(activityId: string): { start: number; end: number } | null {
    return ACTIVITY_EVENT_TIMES[activityId] ?? null;
}

/**
 * Formats a timestamp as a date string (e.g., "Nov 13, 2025")
 */
export function formatEventDate(timestamp: number): string {
    const date = new Date(timestamp * 1000);
    return date.toLocaleDateString("en-US", {
        month: "short",
        day: "numeric",
        year: "numeric",
    });
}

