/**
 * Extracts the permanent zone prefix from a zone ID
 * e.g., "permanent_sidestory_1_zone1" -> "permanent_sidestory_1"
 */
export function getPermanentZonePrefix(zoneId: string): string | null {
    const match = zoneId.match(/^(permanent_(?:sidestory|sub|main)_\d+)_zone\d+$/i);
    return match?.[1] ?? null;
}

/**
 * Gets the event name for a permanent zone
 */
export function getPermanentZoneName(zoneId: string): string | null {
    const prefix = getPermanentZonePrefix(zoneId);
    return prefix ? (PERMANENT_ZONE_NAMES[prefix]?.name ?? null) : null;
}

/**
 * Gets the start time for a permanent zone
 */
export function getPermanentZoneStartTime(zoneId: string): number {
    const prefix = getPermanentZonePrefix(zoneId);
    return prefix ? (PERMANENT_ZONE_NAMES[prefix]?.startTime ?? 0) : 0;
}

