/**
 * Check if an activity ID is part of a permanent side story
 */
export function isPermanentEvent(activityId: string): boolean {
    return activityId in PERMANENT_EVENTS;
}

/**
 * Get the permanent event info for an activity ID
 */
export function getPermanentEventInfo(activityId: string): PermanentEvent | null {
    return PERMANENT_EVENTS[activityId] ?? null;
}
